mapper.select:
select pcode, TO_CHAR(pdate, 'YYYY.MM.DD HH:MI') pdate, ppwd, title, content, filename, hcode, userid
from board
where hcode = #{hcode}

select count(*) from board
where pcode = #{pcode}
and ppwd = #{ppwd}

mapper.update:
 		update board
		set title = #{title}, content = #{content}, pdate = sysdate
		where pcode = #{pcode}
